import os
def write_file(file_name: str, file_content: bytes, output:list) -> bytes:
	base_name, extension = os.path.splitext(file_name)
	padding = 4
	counter = 0

	while True:
		if counter == 0:
			new_file_name = file_name
		else:
			new_file_name = f"{base_name}_{counter:0{padding}d}{extension}"

		if not os.path.exists(new_file_name):
			with open(new_file_name, 'wb') as f:
				f.write(file_content)
			return b"Successfully wrote data", {"status": {"value": 0, "reason": "Success"}}

		counter += 1

